package org.itc.integracion.util;



public class Crypto {
	
	/**---------------------------------------------
	 * 			encripta
	 * ---------------------------------------------
	 * Encriptara el mensaje que recibe.
	 * 
	 * @param mensaje
	 * @return
	 */
	public static String encriptar(String mensaje) {
		try {
			mensaje = mensaje.trim();
			return "CRYPTO://" +RSA.encripta(mensaje, RSA.clave);
		} catch (Exception e) {
			return mensaje;
		}
	}
	
	/**---------------------------------------------
	 * 				desencripta
	 * ---------------------------------------------
	 * 
	 * @param mensaje
	 * @param clave
	 * @return
	 */
	public static String desencriptar(String mensaje) {
		try {
			mensaje = mensaje.trim();
			if (mensaje != null){
				if (mensaje.indexOf("CRYPTO://") == 0) {
					return RSA.desencripta(mensaje.substring(9), RSA.clave);
				}
			}

			return mensaje;
		} catch (Exception e) {
			return mensaje;
		}
	}
	
	public static void main(String[] args) {
		
		String n = "admin_sociedadf1";
		
		String e = encriptar(n);
		
		System.out.println(e);
		String c = "CRYPTO:"; 
		String d = desencriptar(c);
		System.out.println(d);
		c=	"CRYPTO:";
		d = desencriptar(c);
		System.out.println(d);
		
	}
	
}
